CREATE TRIGGER [dbo].[workflow_masters_insert]
    ON [dbo].[requests]
    AFTER INSERT
    AS
BEGIN
    insert into workflow_masters
        (isActive, isCompleted, requestId, workflowId, workflowLevelId, groupId, multiplePicker, startedOn, completedOn, createdAt,
        updatedAt)
    select 1         isactive,
        0         iscomplete,
        r.id,
        f.workflowId,
        wl.id,
        wl.groupId,
        wl.multiplePicker,
        null      startOn,
        null      completeON,
        getdate() cDate,
        getdate() cDate
    from inserted r
        join forms f on f.id = r.formId
        join workflow_levels wl on f.workflowId = wl.workflowId
END

