    async function issueDate() {
        let data = await preLoad();

        const isIssueDate = data.validity_date_type || '''';
        // For validity
        const validiyPeriod = data.validity_period || '''';
        const validityPeriodSelect = data.validity_period_select || '''';

        // For Cliam validity
        const claimValidityPeriod = data.claim_validity_period || '''';
        const claimValidityPeriodSelect = data.claim_validity_period_select || '''';

        const initialIssueDate = document.getElementById("issuedate").value;

        // Global Variable
        let finalDate;

