                    $("#validityDate").val(convert(baseDate.addYears(input_number)));
                    document.getElementById("final_validityDate").value = baseDate.addYears(input_number).toLocaleDateString("en-US", options);
                }
            }
            if (claimValidityPeriod) {
                var selectValue = claimValidityPeriodSelect;
                Date.prototype.addDays = function (d) {
                    var s = new Date(this.getTime() + d * 86400000);
                    return s;
                };
                // 
                Date.prototype.addMonthsClaim = function (m) {
                    var s = new Date(this);
