        // Validity Period
        Date.prototype.addDays = function (d) {
            var s = new Date(this.getTime() + d * 86400000);
            return s;
        };
        Date.prototype.addMonths = function (m) {
            var s = new Date(this);
            s.setDate(s.getDate() - 1);
            var years = Math.floor(m / 12);
            var months = m - years * 12;
            if (years) s.setFullYear(s.getFullYear() + years);
            if (months) s.setMonth(s.getMonth() + months);
            return s;
        };
        Date.prototype.addYears = function (y) {
            var s = new Date(this);
            s.setDate(s.getDate() - 1);
            s.setFullYear(s.getFullYear() + y);
            return s;
        };
        // 
        Date.prototype.addMonthsClaim = function (m) {
            var s = new Date(this);
            s.setDate(s.getDate());
            var years = Math.floor(m / 12);
            var months = m - years * 12;
            if (years) s.setFullYear(s.getFullYear() + years);
            if (months) s.setMonth(s.getMonth() + months);
            return s;
        };
        Date.prototype.addYearsClaim = function (y) {
            var s = new Date(this);
            s.setDate(s.getDate());
            s.setFullYear(s.getFullYear() + y);
            return s;
        };


        function convert(str) {
            var date = new Date(str),
                mnth = ("0" + (date.getMonth() + 1)).slice(-2),
